    --surface1: #51576d;
    --surface0: #414559;
    --base: #303446;
    --mantle: #292c3c;
    --crust: #232634;
/* non-standard colors */
    --surface1Transparent: rgba(81,87,109,0.741);
    --mist: rgba(35,38,52,0.741);
    --accent: var(--blue);
