    ._2v4IIjPhKL0PDaWaWtjJ1E {
        background: var(--surface0) !important;
    }

    /* mod comment tag */
    span[id^=CommentTopMeta--Mod] {
        color: var(--green) !important;
    }

    /* op comment tag */
    span[id^=CommentTopMeta--OP] {
        color: var(--blue) !important;
