    yt-formatted-string.ytls-error-dialog,
    tp-yt-iron-icon.ytls-error-dialog {
      color: @text !important;
    }

    /* text highlight */
    ::selection {
      background: fadeout(@accent-color, 80%) !important;
      color: @text !important;
    }

    ::-moz-selection {
      background: fadeout(@accent-color, 80%) !important;
      color: @text !important;
    }
  }
}

