    /* Buttons */
    .btn-danger,
    .Button--danger {
      background: var(--color-btn-danger-bg);
    }

    /* Create PR button & New branch button */
    .iRoQzU,
    .ifkUYV,
    .bOZDzN {
      background-color: @green;
      color: @base;
    }

    /* New branch dialog */
    .jenxrv {
      background-color: @mantle;
      color: @text;
    }

    /* Branch drop down */
    .fHiIQb {
      background-color: @surface0;
    }

    /* Compare button & Cancel new branch button */
    .idPXPh,
    .epAEYw {
      background-color: @surface0;
      color: @text;
      border-color: @surface1;
    }

    /* search btn */
    .header-search-button.placeholder {
      color: @text;
    }

    /* Issue tags */
    .hx_IssueLabel {
      --text: @text;
    }

    .hx_IssueLabel not when (@lookup =latte) {
      --lightness-threshold: 0.453;
      --border-threshold: 0.96;
      --border-fadeout: max(
        0,
        min(
          calc((var(--perceived-lightness) - var(--border-threshold)) * 100),
          1
        )
      ); //color: hsl(0deg, 0%, calc(var(--lightness-switch) * 100%)) !important;
      color: var(--text) !important;
      background: rgb(
        var(--label-r),
        var(--label-g),
        var(--label-b)
      ) !important;
      border-color: hsla(
        var(--label-h),
        calc(var(--label-s) * 1%),
        calc((var(--label-l) - 25) * 1%),
        var(--border-fadeout)
      ) !important;
    }

    .hx_IssueLabel when (@lookup =latte) {
      --lightness-threshold: 0.6;
      --background-fadeout: 0.18;
      --border-fadeout: 0.3;
      --lighten-by: calc(
        ((var(--lightness-threshold) - var(--perceived-lightness)) * 100) *
          var(--lightness-switch)
      );
      color: hsl(
        var(--label-h),
        calc(var(--label-s) * 1%),
        calc((var(--label-l) + var(--lighten-by)) * 1%)
      ) !important;
      background: rgba(
        var(--label-r),
        var(--label-g),
        var(--label-b),
        var(--background-fadeout)
      ) !important;
      border-color: hsla(
        var(--label-h),
        calc(var(--label-s) * 1%),
        calc((var(--label-l) + var(--lighten-by)) * 1%),
        var(--border-fadeout)
      ) !important;
    }

    /* Labels */
    .Label {
      color: var(--color-accent-fg);
      background-color: @surface0;
      border-color: transparent;
    }

    .Label--secondary {
      background-color: @surface0;
      color: @text;
    }

    .Label--success {
      color: @base;
      background-color: @green;
    }

    .Label--danger {
      background-color: var(--color-danger-emphasis);
      color: var(--color-btn-danger-text);
    }

    .timeline-comment-header .Label,
    .timeline-comment .Label {
      color: var(--color-fg-muted);
    }

    /* Search */
    .header-search-input {
      color: @text !important;
    }

    .header-search-input::placeholder {
      color: @subtext1 !important;
    }

    /* Tags */
    .topic-tag {
      --color-accent-fg: @accent-color;
      --color-accent-emphasis: @accent-color;
      --color-accent-muted: fadeout(@accent-color, 60%);
      --color-accent-subtle: fadeout(@accent-color, 85%);
    }

    /* Misc */
    .col-md-8 {
      background-color: @base;
    }

    .user-mention,
    .team-mention {
      color: @accent-color;
    }

    .kSmUFR[data-size="small"][data-no-visuals="true"] svg {
      color: @green;
    }
    .starred .starred-button-icon {
      color: @yellow;
    }

    /* Large notification */
    .flash {
      background: @mantle;
      border-color: transparent;
    }

    /* Button Fix */
    .color-bg-subtle {
      background-color: transparent !important;
    }

    .command-palette-details-dialog .color-bg-subtle {
      background-color: @surface0 !important;
    }

    /* Button selection Fix */
    .navigation-item[aria-selected="true"] .color-fg-muted {
      color: @base !important;
    }

    /* Darker bg for new AppHeader */
    .AppHeader {
      background: var(--color-header-bg);
    }

    /* Headers */
    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
      color: @subtext1;
    }

    body,
    .markdown-body {
      & when (@usefont=1) {
        font-family: inter, sans-serif;
      }
    }

    #check-step-header-title {
      color: var(--color-checks-text-link) !important;
    }

    ::selection {
      background: @surface2 !important;
    }

    /* New change visibility box star and watcher preview buttons Fix */
    .btn.color-fg-danger.cursor-default {
      color: var(--color-btn-danger-text) !important;
      background-color: var(--color-btn-danger-bg);
    }

    .btn.color-fg-danger.cursor-default:hover {
      background-color: var(--color-btn-danger-hover-bg);
    }

    .btn.color-fg-danger.cursor-default > svg.octicon {
      color: var(--color-btn-danger-text) !important;
    }

    /* New change visibility box danger buttons Fix */
    :where(
        #repo-visibility-proceed-button-public,
        #repo-visibility-proceed-button-private
      ):not([data-next-stage]) {
      color: var(--color-btn-danger-text);
      background-color: var(--color-btn-danger-bg);
    }

    /* 2FA authentication box header Fix */
    body.session-authentication div.Box-header {
      background-color: inherit;
    }

    /* Footer Logo Fix */
    .footer-octicon > svg:nth-child(1) > path:nth-child(1) {
      fill: @text;
    }

    /* Footer text hover fix */
    .footer nav.col-12 * {
      color: @subtext1 !important;
    }

    /* Notifications */
    .notification-indicator .mail-status {
      background-image: none;
      background-color: @accent-color;
    }

    /* Keyboard hint */
    .bdgUpN,
    .hZMmEi .TextInput-icon,
    .hZMmEi .TextInput-action {
      color: @text;
      background-color: @mantle;
      border-color: @surface0;
    }

    /* Merge icon */
    .fMJQEf {
      color: @text;
      background-color: @overlay0;
    }

    /* Branch indicator */
    .iVNDSi {
      color: @accent-color;
      background-color: fade(@accent-color, 10);
